// This code started from Justin Bakse's second take-home challenge on https://compform.net/strategy/ and is a diversion from my code at https://editor.p5js.org/lees846/sketches/DMPd8PT64

let frequency_slider, amplitude_slider, speed_slider;

// position of furthest left ellipse
let startX = -10;
let startY = 100;

// position of furthest right ellipse
let endX = 300;
let endY = -10;

// hue 
let h = 300;
let hueDirect = true;

function setup() {
  createCanvas(500, 500);
  colorMode(HSB);

  let sides = createP("Number of Sides");
  sideCount_slider = createSlider(3, 28, 3);
  sides.position(510, 50 - 35);
  sideCount_slider.position(510, 50);

  let freq = createP("Frequency");
  frequency_slider = createSlider(0, 100, 15);
  freq.position(510, 50 * 2 - 35);
  frequency_slider.position(510, 50 * 2);

  let amp = createP("Amplitude");
  amplitude_slider = createSlider(0, 200, 50);
  amp.position(510, 50 * 3 - 35);
  amplitude_slider.position(510, 50 * 3);

  let speedAlt = createP("Time Speed");
  speed_slider = createSlider(0, 100, 50);
  speedAlt.position(510, 50 * 4 - 35);
  speed_slider.position(510, 50 * 4);
}

function draw() {
  background(50, 0, 0, 0.02);
  fill(150, 80, 100);
  noStroke();

  let sideCount = sideCount_slider.value();

  for(let i = 0; i < TWO_PI; i+= PI/sideCount){
     makeSquares(i);
  }
}

function makeSquares(ang){
  push();
  translate(width / 2, height / 2);
  scale(0.8);
  rotate(ang);
  let frequency = frequency_slider.value() / 1000;
  let amplitude = amplitude_slider.value();
  let speed = speed_slider.value() / 1000;

  noiseDetail(1, 0.1);

  for (i = 0.02; i < 1; i += 0.04) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    let offsetX = (noise(x * frequency + frameCount * speed)) * amplitude;
    let offsetY = (noise(y * frequency + frameCount * speed)) * amplitude;

    // I wanted to limit my hue from 150Âº to 330Âº
    changeHue();
    if(hueDirect){
      h += 0.005;
    } else if(!hueDirect){
      h -= 0.005;
    }

    fill(h, 70, 80, 0.8);
    rect(x + offsetX, y + offsetY, 10, 10);
  }
  pop();
}

function changeHue(){
  if(h>330){
    hueDirect = false;
  } else if (h<150){
    hueDirect = true;
  }
}

