class Point {
	constructor(i) {
		this.i = i
		this.x = noise(i * frequency, 0) * width;
		this.y = noise(i * frequency, 1000) * height;
		this.diameter = random(7, 16);
		this.colors = stripes[this.i%2]
		this.speed = frameCount/1000
	}
	draw() {
		fill(this.colors);
		ellipse(this.x, this.y, this.diameter, this.diameter);
	}
	move(){
		this.x = noise(this.i * frequency + this.speed, 0) * width
		this.y = noise(this.i * frequency + this.speed, 1000) * height
		this.speed = frameCount/100
	}
}

