function drawBranch(length) {
  if (length < 4) {
    return;
  }

  // draw this branch
  strokeWeight(length / 10);
  myTurtle.moveForward(length * 0.5);
  myTurtle.turnLeft(10);
  myTurtle.moveForward(length * 0.6);

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(random(10, 35));
