var waveSys;
var W = 500, H = 500;

function setup() {
  createCanvas(W, H);
	smooth(8);
	frameRate(30);
	strokeWeight(0.3);
	stroke(255);
	noFill();

	waveSys = new NoisyWaves(0.02,0.008);
}

function draw() {
  background(0);
	waveSys.run();
}

function windowResized(){
	waveSys.adjust();
}


function NoisyWaves(xscale,yspeed){
	this.waves = [];
	this.N = 120;
	for (var i = 0; i < this.N; i++){
		let y_attn = map(i,0,this.N,-H*0.12,H*0.3);
		this.waves.push(new NoisySine(xscale,yspeed,y_attn,i*0.005));
	}
}

NoisyWaves.prototype.run = function(){
	for (var i = 0; i < this.N; i++){
		this.waves[i].display();
	}
}
//---call this function below when the window is resized---
NoisyWaves.prototype.adjust = function(){
	for (var i = 0; i < this.N; i++){
		this.waves[i].y_attn = map(i,0,this.N,-H*0.12,H*0.3);
	}
}



function NoisySine(x_scale_,y_speed_,y_attn_,offset_){
	this.N = 256;
	this.x_scale = x_scale_;
	this.y_speed = y_speed_;
	this.y_attn = y_attn_;
	this.offset = offset_;
}


NoisySine.prototype.display = function(){
	var r = 160;
	push();
	translate(W/2,H/2);
	beginShape();
	for (var i = 0; i < this.N; i++){
		let x = norm(i,0,this.N)*W/1.34;
		let y_off = lerp(-this.y_attn,this.y_attn,noise(x*this.x_scale,frameCount*this.y_speed + this.offset));

		let theta = radians(x) + PI/2;
		x = r * sin(theta);
		y = r * cos(theta)+y_off/1.6;
		if(frameCount < 90){y = r * cos(theta)+y_off/2};
		if(frameCount < 60){y = r * cos(theta)+y_off/3.2};
		if(frameCount < 30){y = r * cos(theta)+y_off/4.8};
		if (y > H/2) {y = y-y_off/16};
		if (y < -H/2) {y = y+y_off/16};
		curveVertex(x,y);
	}
	endShape();

	pop();
}

