function drawImage() {
  for (let j = 0; j < myImage.height; j++) {
    let row = "";
    for (let i = 0; i < myImage.width; i++) {
      const pixelIndex = (i + j * myImage.width) * 4;
      const r = myImage.pixels[pixelIndex + 0];
      const g = myImage.pixels[pixelIndex + 1];
      const b = myImage.pixels[pixelIndex + 2];

      const avg = (r + g + b) / 3;

      const len = density.length;
      const charIndex = floor(map(avg, 0, 255, len, 0));

      const c = density.charAt(charIndex);
      if (c == " ") {
        row += "&nbsp;";
      } else {
        row += c;
      }
    }
    //drawn by div 
    rows.push(createDiv(row));

  }

  myImage.updatePixels();

}
// removing and resetting the rows, we have to keep reference of the div to call remove it later
function clearRows() {
  for(const row of rows) {
    row.remove();
  }
  rows = [];
}


function changeLetters(){

  density = input.value()+'.     ';
  clearRows();
  drawImage();
}

