function growCircle() {

  let hueInc = map(millis(), 0, 1000, 0.001, 0.8);

  // increased spacing = less circles, could be turned into density
  let spacing = 10;

  //circleSize based on time
  //increases based on seconds, and decreases during the latter half of the minute
  let circSize;
  if (second() <= 30)
    {
  circSize = map(second(),0,30,5,10);
    }
  else if (second() > 30)
  {circSize = map(second(),31,60,10,5); }

  // radius of the overall shape, not each circle
  //increases based on seconds, and decreases during the latter half of the minute
  let rad;

    if (second() <= 30)
    {
  rad = map(second(),0,30,20,300);
    }
  else if (second() > 30)
  {rad = map(second(),31,60,300,20); 
  }


  // hue variable
  let h = 0; 

  // making a grid starting at "spacing" : centers grid on the canvas 
  for(let y = spacing; y < height; y += spacing){
    for(let x = spacing; x < width; x += spacing){

      // setting it to only draw with a circle's distance from center
      if(dist(x, y, width/2, height/2) < rad){

        // fill opacity based on position
        if (x%4){
        fill(h, 80, 100, 0.8); }
        else {
          fill(h, 80, 100, 0.5); }


      ellipse(x, y, circSize);

        // step up the hue each time to create a rainbow effect
        // if it goes through all 360Â°, reset back to 0Â°
        if(h >= 360){
          h = 0;    // could also do -= to reverse the gradient?
        } else{
          h+=hueInc;   // increment over which hue is gradiated
        }
      }
    }
  }
}

function oppGrowCircle() {

  let hueInc = map(millis(), 0, 1000, 0.001, 0.8);

  // increased spacing = less circles, could be turned into density
