  background(255);


  translate(-240,-250,0);

  //load image location
  img.loadPixels();

  const adjustedClarity = params.clarity + 2;

  for (y = 0; y < img.height; y+=adjustedClarity) {
    for (x = 0; x < img.width; x+=adjustedClarity) {

      // i for pixel 
      var i = (y * img.width + x) * 4;

      let r = img.pixels[i];
      let g = img.pixels[i + 1];
      let b = img.pixels[i + 2];
      let a = img.pixels[i + 3];

      //lower the denominator, the more you see the picture 
      c=(r+g+b)/3;

      stroke(c,c+10*2,c*20)
      // stroke(c,c*10,c*20);

      let xx=width/2+20;
      let yy=height/2+30;

      // strokeWeight(1);

      //adjusting the x value on noise, the higher, the more the lines form a line.

      n=noise(Math.sqrt(x+xx*20)*2,10*y+yy*Math.sqrt(y+yy*2),millis()/2000)*params.space;

      // n=noise(Math.sqrt((x-xx)*(x-xx)+(y-yy-1)*(y-yy))*0.1+millis()/2000)*10;

      point(x, y,n);
    }
  }
}

