function draw() {
  background(0);

  let centerX = img.width/2, centerY = img.height/2;
  img_down.loadPixels();
  img_top.loadPixels();
  img.loadPixels();
  for(let col = 0; col < img_down.width; col++) {
    for(let row = 0; row < img_down.height; row++) {
      let down = getQuick(img_down, col, row);
      let downColor = color(down[0], down[1], down[2]);
      let top = getQuick(img_top, col, row);
      let offset = round(map(brightness(downColor), 0, 100, -20, 20));
      if(sum(down) < 765 && sum(top) > 0)
        setQuick(img, col + offset, row, top);
    }
  }
  img.updatePixels();

  image(img, 0, 0);
}
// quick get a pixel
function getQuick(img, x, y) {
  const i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}
// quick set a pixel
function setQuick(img, x, y, c) {
  const i = (y * img.width + x) * 4;

  img.pixels[i + 0] = c[0];
  img.pixels[i + 1] = c[1];
  img.pixels[i + 2] = c[2];
  img.pixels[i + 3] = 255;
}

function sum(arr) {
  let sum = arr[0] + arr[1] + arr[2];
  return sum;
}

