  background(210, 70, 100);

  drawSun();

  // Drawing the mountain range with noise

  fill(100, 10, 40);

  beginShape();
    let xOff = 0;
    // yMaximum is max because the y value increases from the top not the bottom
    let yMaximum, xValue;     // Variables to keep lowest valley

    for(x = 0; x <= width; x++){
      let y = noise(xOff) * 400;

      vertex(x, y + xOff);

      // Looking for the lowest valley
      if(x == 0){
        yMaximum = y + xOff;
        xValue = x;
      } else {
        if(y + xOff > yMaximum){
          yMaximum = y + xOff;
          xValue = x;
        }
      }

      xOff += 0.02;
      // closes the shape at the end of the screen
      if(x >= width){
        vertex(width, height);
        vertex(0, height);
      }
    }
  endShape(CLOSE);

  drawGround();

  // ellipse that shows lowest point
  // push();
  //   fill("red");
  //   ellipse(xValue, yMaximum, 10, 10);
  // pop();

  push();
    stroke(0, 100, 90);
    line(width/2 - 15, 550, xValue, yMaximum + 5);
    line(width/2, 550, xValue, yMaximum + 5);
    line(width/2 + 15, 550, xValue, yMaximum + 5);

