  const d = generateMeasure();


  d[d.length - 1][0] = notes[3];

  // arrange the measures and return
  return [].concat(a, b, c, d);
}

function generateMeasure() {
  const m = [];

  let timeLeft = Tone.Time("1m");

  while (timeLeft.toSeconds() > 0) {
    // choose note //chooses from one of the three arrays 
    /*const change = sample([-1, 1]);
    degree = constrain(degree + change, 0, 6);
    const note = notes[degree];*/

    let note = "";
    if (index == 0){
      note = tonic[Math.floor(Math.random(tonic.length))];
      index ++;
    } else if (index == 1){
      note = subdominant[Math.floor(Math.random(subdominant.length))];
      index ++;
    } else if (index == 2){
      note = dominant[Math.floor(Math.random(dominant.length))];
      index = 0;
    }

    // choose length
    let length = Tone.Time("2n");
    if (Math.random() < 0.5) {
      length = Tone.Time("4n");
    }
    if (length.toMilliseconds() > timeLeft.toMilliseconds()) {
      length = timeLeft;
    }

    // keep track of time
    timeLeft = Tone.Time(timeLeft - length);

    // add the note to the melody
    m.push([note, length]);
  }

  return m;
}

function play(melody) {
  let t = Tone.now();
  for (const note of melody) {
    console.log(note[0], note[1].toNotation());
    if (note[0] !== "rest") {
      synth.triggerAttackRelease(note[0], Tone.Time(note[1]) - 0.1, t);
    }
    t += Tone.Time(note[1]);
  }
}

