function randomInt(min, max) {
  return Math.floor(Math.random() * (max - min) + min);
}

function sample(data) {
  const index = Math.floor(Math.random(data.length));
  return data[index];
}

function constrain(v, min, max) {
  return Math.min(max, Math.max(min, v));
}

module gate(width, height, thickness, frag=0){
    union(){
        rotate([0,-90,0]){
        difference(){
            translate([height-width/2,0,0])
            cylinder(h=thickness, r=width/2, center=true, $fa=1, $fs=0.5, $fn=frag);
            cube([height*2-width-1, width, thickness], center=true);
        }
        translate([height/2-width/4,0,0])
        cube([height-width/2, width, thickness], center=true);
        }
    }
}

//gate(10, 20, 2);

module wall(length, height, thickness=20){
    bottom_height = 10;
    ramp_width = 6;
    ramp_num = floor(length/(ramp_width*2));
    poso = ramp_num%2==0?(length/2-(ramp_num/2-0.5)*ramp_width*2):(length/2-(ramp_num-1)/2*ramp_width*2);

    color("#cacaca"){
    //bottom part
    //translate([0,0,(bottom_height-height)/2])
    //cube([length+2, thickness+2, bottom_height], center=true);
    //main wall
    difference(){
        cube([length, thickness, height]);
        //ramparts
        for(i=[0:(ramp_num-1)*2]){
            translate([poso+i*ramp_width,thickness/2,height-ramp_width/2+1])
            if(i%2==1) {
                scale([0.2,1,0.2])
                cube([ramp_width,thickness+1,ramp_width],center=true);
            }else{
                cube([ramp_width,thickness+1,ramp_width],center=true);
            }
        }
    }
    }
}

module square_tower(size, height) {
    bottom_height = 10;
    mid_height = 6;
    top_height = 9;
    thickness = 3;
    ramp_width = 4;
    spire_height = 40;
    main_height = height-mid_height-top_height;

    translate([0,0,main_height/2]){ //move above the ground
    union(){
        color("#afafb2"){
        // bottom part
        //translate([0,0,(bottom_height-main_height)/2])
        //cube([size+2, size+2, bottom_height], center=true);
        //lower part
        difference(){
            cube([size, size, main_height], center=true);
            // windows
            for(i=[0:3]){
                rotate([0,0,i*90])
                translate([-size/2, 0, main_height/2-16])
                gate(size/5,8,4);
            }
        }
        }
        translate([0,0,main_height/2]){
        color("#afafb2"){
        //mid part
        rotate([0,0,45])
        cylinder(h=mid_height,d1=size*sqrt(2),d2=(size+5)*sqrt(2),$fn=4);
        //top part
        translate([0,0,top_height/2+mid_height])
        difference(){
            cube([size+5, size+5, top_height], center=true);
            translate([0,0,thickness]){
            cube([size+5-thickness, size+5-thickness, top_height], center=true);
            //ramparts
            ramp_num = floor((size+5)/(ramp_width*2));
            poso = ramp_num%2==0?(-(ramp_num/2-0.5)*ramp_width*2):(-(ramp_num-1)/2*ramp_width*2);
            for(col=[0:ramp_num-1]){
                translate([poso+col*ramp_width*2,0,0])
                cube([ramp_width,size+6,4], center=true);
            }
            for(row=[0:ramp_num-1]){
                translate([0,poso+row*ramp_width*2,0])
                cube([size+6,ramp_width,4], center=true);
            }
            }
        }
        }
        //Spire
        rad = (size+5)/2-thickness;
        color("#af9a9f")
        translate([0,0,mid_height+thickness])
        polyhedron(
        points=[[rad,rad,0],[rad,-rad,0],[-rad,-rad,0],[-rad,rad,0],
        [0,0,spire_height]],
        faces=[ [0,1,4],[1,2,4],[2,3,4],[3,0,4],
        [1,0,3],[2,1,3] ]);
        }
    }
    }
}

module stair(length, height, width){
    num = max(round(length/2), round(height/2));
    l = length/num;
    h = height/num;
    for(i=[0:num-1]){
        translate([0,l*i,0])
        cube([width,l,h*(i+1)]);
    }
}

module slope(length, height, width){
    CubePoints = [
        [0,0,0],
        [0,length,0],
        [0,length,height],
        [width,0,0],
        [width,length,0],
        [width,length,height]
    ];
    CubeFaces = [
        [0,1,2],
        [3,4,5],
        [0,1,4,3],
        [0,2,5,3]
    ];
    polyhedron(CubePoints, CubeFaces);
}

