	Frequency: 0,  Amplitude: 50,	Timespeed: 5};

const pane = new Tweakpane.Pane();

pane.addInput(params, "Frequency", { min: 0, max: 50 });
pane.addInput(params, "Amplitude", { min: 0, max: 100 });
pane.addInput(params, "Timespeed", { min: 0, max: 200 });

function draw() {
  background(50);
  ellipseMode(CENTER);

	let frequency = params.Frequency;
	let amplitude = params.Amplitude / 100;
  let timespeed = params.Timespeed / 100;

  noiseDetail(1, 0.5);

  fill(255);
  noStroke();

  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // hint: drive offsetX and offsetY with noise() instead of random()
    let offsetX = (noise(i * frequency, frameCount/20 * timespeed, 0)*10 - 0.5) * amplitude * 10;
    let offsetY = (noise(i * frequency, frameCount/20 * timespeed, 1)*10 - 0.5) * amplitude * 10;

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

