module snowFlake(pseudolines, length) 
{
    interior_angle = (pseudolines - 2 * 180 / pseudolines);
    adjacent = length * .5;
    opposite = tan(interior_angle * .8) * adjacent; 

    for (i = [0 : pseudolines-1])
    {
        rotate ([0, 0, 360 / pseudolines* i]) 
        translate([0, opposite, 0]) 
        translate([-length* .5, 0, 0]) 
        cube([length, 1, 1]);
    }  
}

color("LightBlue") union() 
{ 
   snowFlake(9, 15);  
}

