module dome(pos){


    translate(pos){
        rotate(22, [0,0,1]){
            cylinder(2,14,14,$fn=18);
            translate([0,0, 2]){
                cylinder(5,14,12,$fn=18);
                translate([0,0, 5]){
                   cylinder(5,12,6,$fn=18);

                }
            }
