   translate([0,0, 12] + pos){
        cylinder(2, 6, 6, true);
        translate([0,0, 1]){
            cylinder(1, 7, 7);
        }
   }


    mirror([1,1,0]) clock_f();
  }

  module clock_01 (){
    translate([0,0,-2]){
              cylinder(9, 5,5, $fn=50, true);   
                translate([0,-5,0]){
                    cube([10, 10, 9], true);
                }
    }
 }

  module clock(pos){
        translate(pos){
          rotate(90, [1,0,0]){
          difference(){
                clock_01();
                translate([0,-11,0]){
                     cube([12,10, 20], true);
                }
                }
           }
        }
  }

  module clock_f(){
    difference(){
        clock([0,-11.7, 35]);
        translate([00,-13.8, 35]){
            rotate(90, [1,0,0]){
                cylinder(1,4,4);
                }
        }
    }

}


module cut_tun (pos, h, r){
    translate(pos+[0,0,h-1]){
        rotate(90, [1,0,0]){
            linear_extrude(height = 6000, center = true){
                union(){
                    circle(r, $fn=500, true);
                    translate([0,-h1/2,0]){
                        square([r*2, h], true);
                    }
                }
            }
        }
    } 
}

module cut_arch(h ,r){
    cut_tun(origin, h, r);
    mirror([1,1,0]){
        cut_tun(origin, h, r);
    }
}

//cut_arch();









module cut(r){
        translate([r-0.07*r, r-0.07*r, 0])
            cylinder(100, r, r, $fn=500, true);
}


module pad(pos, size, r, h){

    translate(pos+[0,0,h]){
        translate([0,0,-h]){
            difference() {
                cube([size, size, 0.5],true); // Outer
                cut_edge(r);
            }


            translate([0, 0, 0.5])
                difference() {
                    cube([size-2, size-2, 0.5], true); // Padding
                    cut_edge(r-0.04*r);
                }
            translate([0, 0, h/2])
                difference() {
                    cube([size-4, size-4, h], true); // Inner
                    cut_edge(r-0.08*r);
                }
            }
        mirror([0, 0, 1]){
            translate([0,0,-3]){
            difference() {
                cube([size, size, 0.5],true); // Outer
                cut_edge(r);
            }


            translate([0, 0, 0.5])
                difference() {
                    cube([size-2, size-2, 0.5], true); // Padding
                    cut_edge(r-0.04*r);
                }
            translate([0, 0, h/2])
                difference() {
                    cube([size-4, size-4, h], true); // Inner
                    cut_edge(r-0.08*r);
                }
            }
        }
    } 
} 

module cut_edge(r){
cut(r);
mirror([1,1,0]) cut(r);
mirror([1,0,0]) cut(r);
mirror([0,1,0]) cut(r);
}

