const points = [];

function setup() {
  createCanvas(400, 400);
  noStroke();
  ellipseMode(CENTER);
  colorMode(HSB, 1);

  noLoop();
}

function draw() {
  background(1, 0, 0.2);

  let noiseFrequency = 2;

  for (let i = 0; i < 100; i++) {

    placePoint(i, width, height, noiseFrequency);

    fill(points[i].hue, 1, 1);
    ellipse(points[i].x, points[i].y, points[i].d);
  }
}

function placePoint(i, w, h, freq) {
  let x = noise(i * freq, 0) * w;
  let y = noise(i * freq, 1000) * h;

  let param = (random() + random()) / 2;
  let diameter = map(param, 0, 1, 4, 16);

  points.push({
    x: x,
    y: y,
    d: diameter,
    hue: (param - 0.15) % 1
  })
}

