let myTurtle;
let numbers = 100;
let moveFactor = 2;

function setup() {
  createCanvas(windowWidth, windowHeight);
  myTurtle = new Turtle(0, 0);
}

function draw() {
  blendMode(DIFFERENCE);
  background(30);

  spiro(width/2, height/2, 1, 12, color('rgba(90,90,150,0.2)'), 'left');

    spiro(width/2, height/2, 4, 6, color('rgba(90,150,90,0.1)'), 'right');

    circleSpiro(width/2 - 150,height/2 - 140, 1, 1, 6, color(180,255,120,80));

   circleSpiro(width/2 + 150,height/2 - 140, 60, 60, 60, color(180,255,120,8));

     circleSpiro(width/2 + 150,height/2 + 140, 50, 50, 20, color(120,240,180,5));

       circleSpiro(width/2 - 150,height/2 + 140, 90, 100, 38, color(120,240,180,5));

      circleSpiro(width/2,height/2, width/2, 150, 60, color(255,120,180,2));


  noLoop();
}

function circleSpiro(x, y, distance, angleTurn, numbers, color) {
  stroke(color);
  push();
    myTurtle.penUp();
    myTurtle.moveTo(x, y);
    myTurtle.penDown();
    for (let i = 0; i < numbers; i++) {
      for (let j = 0; j < 360; j++) {
        myTurtle.moveForward(distance);
        myTurtle.turnRight(angleTurn);
      }
      myTurtle.turnLeft(360/numbers);
    }
  pop();
}

function spiro(x,y, dist, angle, color, direction) {
  push();
    stroke(color);
    strokeWeight(4);
    myTurtle.penUp();
    myTurtle.moveTo(x, y);
    myTurtle.penDown();
    for (let i = 0; i < width * 2; i++) {
      myTurtle.moveForward(dist + i * 0.1);
      direction === 'left' ? myTurtle.turnLeft(angle) : myTurtle.turnRight(angle);

    } 
  pop();
}

