  face.ellipse(w * 0.5 + 0, h * 0.5 + 1, 2, random(2, 5)); // nose
  face.ellipse(w * 0.5 - 2, h * 0.5 + 1, 2, 2); // left cheek
  face.ellipse(w * 0.5 + 2, h * 0.5 + 1, 2, 2); // right cheek
