// for green elephant forest, use the commented out background and stroke colors

let myTurtle;
let elephantImg;

let wave1;
let wave2;

function preload(){
  elephantImg = loadImage('elephant.jpg');
}

function setup() {
  createCanvas(1000, 645);

  stroke(0);
  // noStroke();
  pixelDensity(1);

  // noLoop();
  frameRate(0.2);
  myTurtle = new Turtle();
}

function draw() {
  background(120);
  // background(70, 120, 100);


  // waves for wind effect
  wave1 = noise(frameCount*0.1);
  wave2 = noise(frameCount*0.08 + 3);
  console.log(wave1, wave2);

  elephantImg.loadPixels();

  // Loop structure and performance check from https://compform.net/pixels/
  const start = performance.now();

  // loop over every x,y pixel coordinate in the image
  for (let x = 1; x < width; x += 12) {
    for (let y = 1; y < height; y += 12) {
      const pixelRed = getQuick(elephantImg, x, y)[0];
      const pixelGreen = getQuick(elephantImg, x, y)[1];
      const pixelBlue = getQuick(elephantImg, x, y)[2];

      const val = (pixelRed + pixelGreen + pixelBlue)/3;

      // Drawing trees based on pixel values
      if (val <= 40){
        stroke(0);
        // stroke(0, val, 0);
        const treeCount = 5;
        for(let copy = 0; copy < treeCount; copy++){
          drawTree(x, y, (360/treeCount)*copy);
        }
      } else if ( val > 40 && val <= 80){
        stroke(val);
        // stroke(val - 80, val, val - 40);
        const treeCount = 2;
        for(let copy = 0; copy < treeCount; copy++){
          drawTree(x, y, -90);
        }
      } else if (val > 80 && val <=100){
        stroke(val);
        // stroke(val - 80, val, val - 40);
        const treeCount = 3;
        for(let copy = 0; copy < treeCount; copy++){
          drawTree(x, y, (360/treeCount)*copy);
        }
      } else if (val > 240){
        // stroke(80, 200, 120);
        stroke(200);
        const treeCount = 4;
        for(let copy = 0; copy < treeCount; copy++){
          drawTree(x, y, (360/treeCount)*copy - 90);
        }
      }
    }
  }

  const end = performance.now();

  console.log(`took ${floor(end - start)} ms`);

  // CAUTION: this saves 240 frames of this sketch to your downloads folder
  // Saves one frame every 5 seconds
  // when noLoop() is deactivated
  saveFrame("elephant", frameCount, "png", 240);
  // noLoop();

}

// getQuick from https://compform.net/pixels/
// find the RGBA values of the pixel at x, y in the img.pixels array
function getQuick(img, x, y) {
  const i = (y * img.width + x) * 4;
  return [
    img.pixels[i],
    img.pixels[i + 1],
    img.pixels[i + 2],
    img.pixels[i + 3],
  ];
}


function drawTree(x, y, angle){
  myTurtle.penUp();
  myTurtle.moveTo(x, y);
  myTurtle.turnTo(angle);
  myTurtle.penDown();
  const branchLength = 20;
  drawBranch(branchLength);

}

// drawBranch modified from Justin Bakse's "Recursive Tree I" sketch on https://compform.net/turtles/
function drawBranch(length) {
  // this return function determines level of detail
  // higher number = less detail
  // if max length = branch length, there will be only 1 level of tree
  if (length < 10) {
    return;
  }

  // draw this branch
  myTurtle.moveForward(length);

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(map(wave2, 0, 1, -15, 15));
  drawBranch(length * 0.85);
  myTurtle.popState();

  // right child
  myTurtle.pushState();
  myTurtle.turnRight(map(wave2*-1, 0, 1, -15, 15));
  drawBranch(length * 0.85);
  myTurtle.popState();
}


// saveFrame - a utility function to save the current frame out with a nicely formatted name
// from https://compform.net/animation
// format: name_####.extension
  // name: prefix for file name
  // frameNumber: number for the frame frameCount, will be zero padded
  // extension: jpg or png, controls file name and image format
  // maxFrame: checked against frameNumber, frames beyond maxFrame are not saved
function saveFrame(name, frameNumber, extension, maxFrame) {
  // don't save frames once we reach the max
  if (maxFrame && frameNumber > maxFrame) {
    return;
  }

  if (!extension) {
    extension = "png";
  }
  // remove the decimal part (just in case)
  frameNumber = floor(frameNumber);
  // zero-pad the number (e.g. 13 -> 0013);
  const paddedNumber = ("0000" + frameNumber).substr(-4, 4);

  save(name + "_" + paddedNumber + "." + extension);
}

