function randomGradients() {
  for (let x = 0; x < width; x+=boxes) {
    for (let y = 0; y < height; y+=boxes) {
        drawRandomRect(x,y,boxes, random([-1,1]));
    }
  }

  updatePixels();  
}

function drawRandomRect(x,y,w, randomFactor) {
  if (x + w < width && y + w < height) {
    computePixels(x, (x+w), y, (y+w), randomFactor);
  }
  else {
    if (x + w > width && y + w < height) {
      computePixels(x, width, y, (y+w), randomFactor);
    }
    else if (x + w < width && y + w > height) {
      computePixels(x, (x+w), y, height, randomFactor);
    }
  }
}

function computePixels(x,xLimit, y, yLimit, randomFactor) {
    for (let xs = x; xs < xLimit; xs++) {
      for (let ys = y; ys < yLimit; ys++) {
          let xmap = map(xs, 0, width, 0, 255);
          let ymap = map(xs, 0, width, 0, 255);
          let index = (ys * width + xs) * 4;
          pixels[index] = randomFactor === 1 ? ymap : xmap;
          pixels[index + 1] = randomFactor === -1 ? xmap : random(0,25);;
          pixels[index + 2] =  randomFactor === 1 ? xmap : random(120,200);
          pixels[index + 3] = 255; 
      }
    }
}

