function drawBuilding(x, y, h) {


  // let x = random(0, 400);
  // let y = random(200, 350);
  // let w = random(2, 120);
  let w =  random(2)>1?random(40, 140) : h
  // let h = random(40, 240);
  let l = random(2)>1?random(40, 140) : h
  // let l = h
  let c = color(random(colors))
  c.setAlpha(100)
  fill(c)
  persRect(x, y, w, h, perspectiveL)
  c = color(random(colors))
  c.setAlpha(100)
  fill(c)
