function createWorld(){
  let noiseValue = 0;
  //Centering world
  translate(-(mapSize*tileSize)/2,-(mapSize*tileSize)/2,0);
  //Creating grid of tiles
  noStroke();

  let xnoise = xPos;// map the perlin offset
  //grid x
  for(let i = 0; i < mapSize; i++){
      let ynoise = yPos;
     for(let j = 0; j< mapSize; j++){

       let h = 12*noise(xnoise,ynoise);
       if(h < water){
          //water
          fill("#92cde2");
          h=4;
        }else if(h < grass && h>sand){
          //sand
          fill("#f8d94a");
          h=4;

        }else if(h>mountain){
          fill("#7abb96");
          h=10;       
        }else{
          //grass
         fill("#d0e34a");
          h=7;
        }

       translate(tileSize,0,h/2);
       box(tileSize,tileSize, h);
       translate(0,0,-h/2);
       //perlin noise
       ynoise += 0.15;
     }
    xnoise +=  0.15;

    translate(-tileSize*mapSize,tileSize,0); 
  } 
}

