// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js

var _glowBorder = 1.1;
var _glowColor;

var frags = [];

function setup() {
  createCanvas(640, 480);
  noStroke();
  rectMode(CENTER);
  angleMode(DEGREES);

  _glowColor = color(255);
}

function draw() {
  background(0);

  // generate fragments
  if(frameCount % 10 === 0 && random(100) < 10) {
    generateFrag(-100, random(height), random(20, 60), random(20, 60), random(0.2, 1), getRanCol(random(120, 180)), random(0.1, 0.5));
  }
  // update fragments
  for(let i in frags) {
    // update fragment
    if(frags[i].update()) {
      frags.splice(i, 1);
      continue;
    } else { // draw the fragment
      frags[i].draw();
    }
  }
}

// generate a new fragment
function generateFrag(x, y, w, h, spd, color, rot) {
  let p1 = random(-w, w), p2 = random(-h, h), p3 = random(-w, w), p4 = random(-h, h);
  frags.push(new frag(x, y, w, h, [p1, p2, p3, p4], spd, color, rot));
}

class frag {
  constructor(x, y, w, h, vtx, spd, color, rot = 1) {
    this.pos_x = x;
    this.pos_y = y;
    this.width = w;
    this.height = h;
    this.vtx = [vtx[0], vtx[1], vtx[2], vtx[3]];
    this.ptsX = [x + vtx[0], x - w, x + vtx[2], x + w];
    this.ptsY = [y - h, y + vtx[1], y + h, y + vtx[3]];
    this.spd = spd;
    this.dir = 0;
    this.rot = rot;
    this.color = color;
    this.count = frameCount;
  }
  update() {
    // move the fragment
    this.pos_x += this.spd;
    this.dir += this.rot;
    let x = this.pos_x, w = this.width, vtx = this.vtx;
    this.ptsX = [x + vtx[0], x - w, x + vtx[2], x + w];

    // check if leave the canvas
    return ifLeave(this);
  }
  draw() {
    let x = this.pos_x, y = this.pos_y, xs = this.ptsX, ys = this.ptsY;
    let x1 = xs[0] - x, x2 = xs[1] - x, x3 = xs[2] - x, x4 = xs[3] - x;
    let y1 = ys[0] - y, y2 = ys[1] - y, y3 = ys[2] - y, y4 = ys[3] - y;

    // draw glow effect
    drawGlow(x, y, [x1, x2, x3, x4], [y1, y2, y3, y4], this.color, this.spd, this.dir, this.count);
    push();
    translate(x, y);
    rotate(this.dir);
    fill(this.color);
    beginShape();
    vertex(x1, y1);
    vertex(x2, y2);
    vertex(x3, y3);
    vertex(x4, y4);
    endShape();
    pop();
  }
}

function drawGlow(x, y, ptX, ptY, color, spd, dir, count) {
  let thre = 240;
  let step = sin(map((frameCount - count) % thre, 0, thre, 0, 180)) * 15;
  let glow_color = lerpColor(color, _glowColor, 0.5);

  push();
  translate(x, y);
  rotate(dir);
  for(let lay = 0; lay < step; lay ++) {
    glow_color.setAlpha(map(lay, 0, step, 60, 0));
    fill(glow_color);
    scale(map(lay, 0, 20, 1, _glowBorder));
    beginShape();
    // curveVertex(ptX[0], ptY[0]);
    // curveVertex(ptX[1], ptY[1]);
    // curveVertex(ptX[2], ptY[2]);
    // curveVertex(ptX[3], ptY[3]);
    vertex(ptX[0], ptY[0]);
    vertex(ptX[1], ptY[1]);
    vertex(ptX[2], ptY[2]);
    vertex(ptX[3], ptY[3]);
    endShape(CLOSE);
  }
  pop();
}

// check if the object leave the canvas
function ifLeave(obj) {
  let x = obj.pos_x, y = obj.pos_y, w = obj.width, h = obj.height;
  if(x < - 100 - w || x > width + w || y < 0 - h || y > height + h) {
    return true;
  }else return false;
}

// get a random color
function getRanCol(alp = 255) {
  return color(round(random(255)), round(random(255)), round(random(255)), alp);
  // return 'rgb(' + round(random(255)) + ',' + round(random(255)) + ',' + round(random(255)) + ')';
}

