function setQuick(img, x, y, c) {
	const i = (y * img.width + x) * 4;

	img.pixels[i + 0] = c[0];
	img.pixels[i + 1] = c[1];
	img.pixels[i + 2] = c[2];
	img.pixels[i + 3] = c[3];
}

function optFunc() {
	img = createImage(500, 500);
	img.loadPixels();

	for (let y = 0; y < img.height; y++) {
		for (let x = 0; x < img.width; x++) {
			let a =
				(x + frameCount * params.z * params.x) %
				y % pow(params.y, 2)*abs(params.a*
			  noise(frameCount/10, x/100, y/100))*3
			let b = sin(a/params.c*(params.b+1))*1000
			let c = a*tan(b*a*pow(1/a*0.1, params.c))
			let d = [a, b, c, 255]

			setQuick(img, x, y, d);
		}
	}

	img.updatePixels();

	noSmooth();
	image(img, 0, 0, width, height);
}

