    color(32,77,131),
    color(57,79,58),
    color(199,165,56),
    color(170,38,27),
    color(6,20,29),
    color(0),

  ];

  deckColors = shuffle(deckColors) 
  deckFigures = shuffle(deckFigures);

  colorFromDeck();
}

function draw() {

  background(241,238,236);

  for(i = 0; i < deckFigures[1]; i++){

    var x = random(width);
    var y = random(height);

    push();
    strokeWeight(3);
    line(x, y, random(width), random(height));
    pop();

    if(deckFigures[1] % 2 == 0){
       drawBranches(x,y);
    }
  }

  for(i = 0; i < deckFigures[0]; i++){

    var s = random(50,60);

    push();
    noStroke();
    colorFromDeck();
    //fill(deckColors[i]);
    ellipse(random(500), random(500), s, s);
    pop(); 
  }

  for(i = 0; i < deckFigures[3]; i++){    

    push();
    noStroke();
    colorFromDeck();
    triangle(random(500),random(500), random(500),random(500),random(500),random(500)); 
    pop();
  }

