function drawBranches(x, y){
    for(i = 0; i < 3; i++){
    push();
    strokeWeight(3);
    line(x, y, random(width/2), random(height/2));
    pop();
  }
}

function colorFromDeck() {
  // find the value at the current position in the deck
  var v = deckColors[position];

  // change the position for next time
  position++;

  // if that was the last value, shuffle and start over from the top
  if (position == deckColors.length) {
    deckColors = shuffle(deckColors);
    position = 0;
  }

  // return the value
  fill(v);
  return v;

}

var source, markov, article = "";

function preload() {
  source = loadStrings("seeds.txt");
}

function setup() {
  createCanvas(400, 400);
  textAlign(LEFT, TOP);
  textStyle(BOLD);
  textFont('helvetica');

  // set up the markvo model
  markov = RiTa.markov(2);
  markov.addText(source.join(' '));

  // initialize article
  let tokens = RiTa.tokenize(source.join(' '));
  article = tokens[floor(random(tokens.length - 1))];
}

function draw() {
  background(220);
  // generate texts gradually
  if(frameCount % 60 === 0) {
    let tokens = RiTa.tokenize(article);
    let probs = markov.probabilities(tokens);
    let newString = selectWord(probs);
    article = article.concat(newString);
    // noLoop();
  }
  text(article, 40, 40, width - 80, height - 80);
}

function selectWord(probs) {
  let seed = random(), chance = 0, word = '';
  for(let key in probs) {
    let min = chance;
    chance += probs[key];
    if(seed > min && seed < chance && key !== '.') {
      word = key;
      break;
    }
  }
  if(word !== '') word = ' ' + word;
  return word;
}

