var particles = [], particles3D = [];

// set up the panel
const panelParam = {
  Color: 0,
  Sun_Size: 5,
  Intensity: 5,
}
const pane = new Tweakpane.Pane();

pane.addInput(panelParam, 'Color', {min: 0, max: 1, step: 0.01});
