    if (me.role === "player1" || me.role === "player2") {
      return;
    }

    if (!players.find((p) => p.role === "player1")) {
      me.role = "player1";
      return;
    }
    if (!players.find((p) => p.role === "player2")) {
      me.role = "player2"; 
      return;
    }
}

