function filterCamera(){

  push();
  background(0);
  translate(width,0);
  scale(-1, 1);
  image(capture, 0, 0, width, height);
  positions = tracker.getCurrentPosition();

  capture.loadPixels();
  var stepSize = 15;

  for (var x = 0; x < capture.width; x += stepSize) {
    for (var y = 0; y < capture.height; y += stepSize) {
      var index = ((y*capture.width) + x) * 4;
      var red = capture.pixels[index + damageFX];
      var green = capture.pixels[index];
      var blue = capture.pixels[index];
      fill(red, green, blue);
      rect(x, y, stepSize, stepSize);
    }
  }

  pop();

}

