class Flash {
	constructor() {
		this.posX = random(width)
		this.posY = random(height)
		this.rotate = params.rotate
		this.p = random(40)
		this.newP = this.p
		this.k = 0.1
		this.newK = this.k
		this.speed = random(5)
		this.newSpeed = this.speed
	}

	check(){
		this.newP = this.p + params.p
		this.newK = this.k + params.k
		this.newSpeed = this.speed * params.speed
		this.rotate = params.rotate
	}

	animate(){
		this.posX -= this.newSpeed
		this.posY += sin(this.newSpeed*10 + frameCount/100)*2

		if(this.posX < -100){
			this.posX = width + 100
		}
		if(this.posY > height + 100){
			this.posY = - 100
		}
		if(this.posY < -100){
			this.posY = height + 100
		}
	}

	draw() {
		push()
		translate(this.posX, this.posY)
		rotate(this.rotate)
		scale(1, sin(this.newSpeed*10 + frameCount))
		noStroke()

		let step = 1/this.newP
		for (let t = 0; t < this.newK; t += step) {
			let x = 2 * this.newP * t * t
			let y = 2 * this.newP * t

			for (let j = -y; j < y; j += 5) {
				let bright = map(dist(0, 0, x, j), 0, 200, 300, 0)
				let b = map(dist(0, 0, x, j), 0, 200, 1.8, 0)
				fill(bright - noise(frameCount/100 + t/10, j/100, this.p*5)*100)
				circle(x, j, b)
			}

			let bright = map(dist(0, 0, x, y), 0, 200, 255, 0)
			fill(bright - noise(frameCount/10 + t/10)*200)
			circle(x, y, 1)
			circle(x, -y, 1)
		}
		pop()
	}
}

