function setup() {
  var side = windowWidth< windowHeight ? windowWidth : windowHeight ;
  createCanvas(side,side); 
  noStroke();
  colorMode(HSB,360,1.0,1.0);
  rectMode(CENTER);
  myScale = width < height ? width/foo : height/foo; 
} 

function draw() {
  background(panelParam.Color, 1, 0.5);
  let nShape = 50 + map(panelParam.Density, 0, 1, 10, 200);
  for(let i = 0; i < nShape; i++){
    let theta = 2.39996 * i * map(panelParam.Density, 0, 1, 2, 0.5);
  	let r = map(panelParam.InnerSize, 0, 1, 0, 200) + myScale/map(panelParam.Density, 0, 1, 2, 10) * i;
    let d = myScale * (5 + panelParam.Oscillation * sin(frameCount/50 + r));

    let step = i / nShape;

    let x = width/2 + r * sin(theta), y = height/2 + r * cos(theta);
    push();
    fill(panelParam.Color,
         1 - map(panelParam.OuterSize, 0, 1, 1, 4) * step,
         0.5 + map(panelParam.OuterSize, 0, 1, 0.5, 2) * step
    );
    translate(x, y);
    rotate(theta);
    if(panelParam.IfCircle) circle(0, 0, d * 2);
    else polygon(0, 0, d, panelParam.EdgeNum);
    pop();
  }
}

// draw regular polygon
function polygon(x, y, radius, npoints) {
  let angle = TWO_PI / npoints;
  beginShape();
  for (let a = 0; a < TWO_PI; a += angle) {
    let sx = x + cos(a) * radius;
    let sy = y + sin(a) * radius;
    vertex(sx, sy);
  }
  endShape(CLOSE);
}

