for (var col = 0; col < 10; col++) {
  for (var row = 0; row < 10; row++) {
    var center = new Point(col * 100 + (row % 2) * 40, row * 100);
    petal(center, 24);
    bud(center, 0);
  }
}

function bud(center, r) {
  var radiusDelta = values.maxRadius - values.minRadius;
  var pointsDelta = values.maxPoints - values.minPoints;
  var radius = values.minRadius + Math.random() * radiusDelta;
  var points = values.minPoints + Math.floor(pointsDelta);
  var path = createBlob(center + new Point(r, r), radius, points);
  var lightness = Math.random();
  var hue = "#ff2b6b";
  path.fillColor = {
    hue: hue,
    saturation: 1,
    lightness: 0.4,
  };
  path.strokeColor = "#000000";
  path.strokeWidth = 4;
}

function petal(center, r) {
  var radiusDelta = values.maxRadius - values.minRadius;
  var pointsDelta = values.maxPoints - values.minPoints;
  for (var i = 0; i < values.paths; i++) {
    var radius = values.minRadius + Math.random() * radiusDelta;
    var points = values.minPoints + Math.floor(pointsDelta);
    var x = r * Math.sin(36 * i);
    var y = r * Math.cos(36 * i);
    var path = createBlob(center + new Point(x, y), radius, points);
    var lightness = Math.random();
    var hue = "#ff2b6b";
    path.fillColor = {
      hue: hue,
      saturation: 0.9,
      lightness: 0.7,
    };
    path.strokeColor = "#000000";
    path.strokeWidth = 4;
  }
}

function createBlob(center, maxRadius, points) {
  var path = new Path();
  path.closed = true;
  for (var i = 0; i < points; i++) {
    var delta = new Point({
      length: maxRadius * 0.9 + Math.random() * maxRadius * 0.5,
      angle: (360 / points) * i,
    });
    path.add(center + delta);
  }
  path.smooth();
  return path;
}

