function middleRandom(min, max, rolls) {
  let v = 0;
  for (let roll = 0; roll < rolls; roll++) {
    v += random(min, max);
  }
  return v / rolls;
}

function roundTo(v, n) {
  return Math.round(v / n) * n;
}

