class customFace {
  constructor(faceW, faceH) {
    this.w = faceW;
    this.h = faceH;
    this.eyeSize = faceW * 0.2;
    this.eyeSizeH = faceW * 0.2;
    this.eyeFill = color(68,68,68);
  }

  renderFace(colorV) {
    push();
      fill(220,100, colorV);
      noStroke();
      translate(0, this.h/2);
      beginShape();
        vertex(0, -this.h);
        bezierVertex(0.8 * this.w, -this.h, this.w, -this.w, this.w, -0.69*this.w);
        bezierVertex(this.w, -0.38*this.w, 0.8 * this.w, 0, 0, 0);
        bezierVertex(-0.8 * this.w, 0, -this.w, -0.38*this.w, -this.w, -0.69 * this.w);
        bezierVertex(-this.w, -this.w, -0.8 * this.w, -this.h, 0, -this.h);
      endShape();
    pop();

    if (colorV < 122) {
      this.eyeFill = lerpColor(this.eyeFill, color(255,255,255), 0.3);
    }
    else {
      this.eyeFill = lerpColor(this.eyeFill, color(68,68,68), 0.2);
    }
  }

  drawEyes() {
    if (!sleeping) {
      this.eyeSizeH = lerp(this.eyeSizeH, this.eyeSize, 0.2);
    }
    else {
      this.eyeSizeH = lerp(this.eyeSizeH, this.eyeSize * 0.25, 0.2);
    }
    push();
      noStroke();
      fill(this.eyeFill);
      push();
        if(angry) {
          rotate(0.08)
        }
        translate(-this.w*0.55, -this.h*0.15)
        ellipse(0, 0, this.eyeSize, this.eyeSizeH);
      pop();
      push();
        if(angry) {
          rotate(-0.08)
        }
        translate(this.w*0.55, -this.h*0.15)
        ellipse(0, 0 , this.eyeSize, this.eyeSizeH);
      pop();
    pop();
  }

  updateFace(w, h) {
    this.w = lerp(this.w, w, 0.2);
    this.h = lerp(this.h, h, 0.2);
    this.eyeSize = lerp(this.eyeSize, w * 0.2, 0.1)
  }

  createNose() {
    push();
      noStroke();
      fill('rgba(255,255,255,0.3)')
      beginShape();
        curveVertex(0, this.h * 0.338 );
        curveVertex(-2, this.h * 0.33 );
        curveVertex(-this.w * 0.08,  this.h * 0.25);
        curveVertex(this.w * 0.08,  this.h * 0.25);
        curveVertex(2, this.h * 0.33 );
      endShape(CLOSE);
    pop();
  }
}

