// multiple pixelization
function multiPixelize(img, widths) {
  img.loadPixels();
  let sumWidth = 0;
  for(let i = 0; i < widths.length; i++) {
    edgePixelize(img, widths[i], img.width - sumWidth * 2, sumWidth);
    sumWidth += widths[i];
  }
  img.updatePixels();
}
// pixelize the edge of the image
function edgePixelize(img, layWidth, totalWidth, start = 0) {
  let edgeNum = round(totalWidth / layWidth);
  let startX = start, startY = start;
  for(let row = 0; row < edgeNum; row ++) {
    if(row === 0 || row === edgeNum - 1) {
      for(let col = 0; col < edgeNum; col++) {
        averageCol(img, startX + col * layWidth, startY + row * layWidth, startX + col * layWidth + layWidth - 1, startY + row * layWidth + layWidth - 1);
      }
    } else {
      averageCol(img, startX, startY + row * layWidth, startX + layWidth - 1, startY + row * layWidth + layWidth - 1);
      averageCol(img, startX + (edgeNum - 1) * layWidth, startY + row * layWidth, startX + edgeNum * layWidth - 1, startY + row * layWidth + layWidth - 1);
    }
  }
}
// calculate the average color of an area and change the color
function averageCol(img, x1, y1, x2, y2) {
  let w = img.width, h = img.height;
  let sumR = [], sumG = [], sumB = [];
  for(let y = y1; y <= y2; y++) {
    for(let x = x1; x <= x2; x++) {
      sumR.push(img.pixels[y * 4 * w + x * 4]);
      sumG.push(img.pixels[y * 4 * w + x * 4 + 1]);
      sumB.push(img.pixels[y * 4 * w + x * 4 + 2]);
    }
  }
  let r = average(sumR), g = average(sumG), b = average(sumB);
  for(let y = y1; y <= y2; y++) {
    for(let x = x1; x <= x2; x++) {
      img.pixels[y * 4 * w + x * 4] = r;
      img.pixels[y * 4 * w + x * 4 + 1] = g;
      img.pixels[y * 4 * w + x * 4 + 2] = b;
    }
  }
}

function average(arr) {
  let sum = 0;
  arr.forEach((x) => sum += x);
  return sum / arr.length;
}

