let t;
let recursionCount = 0;
let angleOffset = 36
let recursionTotal = 3;//change from 1 to 5
let stColor = [0, 255, 255];
function setup() {
  createCanvas(500, 500);
  t = new Turtle(100, 100);
}

function draw() {
  t.moveTo(100, 100);
  t.penDown();
  drawStar(0);

  noLoop();
}

function drawStar(rc) {
  stroke(
    (stColor[0] += 10*pow(2, -recursionTotal)),
    (stColor[1] -= 10*pow(2, -recursionTotal)),
    (stColor[2] -= 2*pow(2, -recursionTotal))
  );
  if (rc < recursionTotal) {

    rc++;
    for (let side = 0; side < 5; side++) {
      for (let i = 0; i < 100 / rc; i++) {
        t.moveForward(1);
        t.turnRight(0.1);
      }
      t.turnLeft(angleOffset)
      drawStar(rc);
      t.turnLeft(-angleOffset)
      t.turnLeft(10 / rc);
      t.turnRight(144);

    }
  }
}

