class cEllipse {
	constructor() {
		this.centerX = random(width)
		this.centerY = random(height)
		this.shortR = min(random(10, 200), random(10, 200))
		this.longR = (random(10, 300) + random(10, 300)) / 2
		this.number = parseInt(min(random(30, 50), random(30, 50)))
		this.angle = parseInt(random(360))
		this.angleGap = (360 / this.number)
		this.rotation = max(random(360), random(360))
		this.rotateSpeed = (random(0.5) + random(0.5)) / 2
		this.colorers = []
		this.thickers = []

		for (let i = 0; i < this.number; i++) {
			this.colorers[i] = random(colorList)
			this.thickers[i] = 3

		}
	}
	draw() {
		push()
		translate(this.centerX, this.centerY)
		rotate(this.rotation)

		for (let k = 0; k < 30; k++) {
			let shade = map(k, 0, 30, 0, 200)
			let scala = map(k, 0, 30, 2.5, 0.5)
			fill(0, 0, 30, shade)
			noStroke()
			ellipse(offsetX * 1.9, offsetY * 1.9, this.longR * scala + 7, this.shortR * scala + 7)
		}

		fill(200)
		noStroke()
		ellipse(offsetX, offsetY, this.longR * 2 + 7, this.shortR * 2 + 7)

		fill(0, 0, 30)
		stroke(50)
		ellipse(0, 0, this.longR * 2 + 7, this.shortR * 2 + 7)

		for (let i = 0; i < this.number; i++) {
			noFill()
			stroke(this.colorers[i])
			strokeWeight(this.thickers[i] * abs(2 * sin(frameCount + i * 10)))
			line(0, 0,
				this.longR * cos(this.angle),
				this.shortR * sin(this.angle))
			this.angle += this.angleGap
		}
		translate(-this.centerX, -this.centerY)
		pop()
	}
	rotater() {
		let xx = mouseX
		let yy = mouseY
		this.rotation = map(atan(yy / xx), 30, 90, 0, 360)
	}
	reset() {
		this.rotation += this.rotateSpeed
	}
}

