class Spot{

  constructor(x, y, inc, dir){
    this.diameter = 0;
    this.x = x;
    this.y = y;
    this.inc = inc;
    this.dir = dir;
    this.hue = 1;
  }

  draw(){
    if(this.dir){
      this.hue = map(this.diameter, MIN_DOT, MAX_DOT, 1, 360);
    }else{
      this.hue = map(this.diameter, MIN_DOT, MAX_DOT, 1, 360);
    }

    fill(color(this.hue, map(noise(this.x, this.y), 0, 1, 0, 100), map(noise(this.y, this.x), 0, 1, 0, 100)));
    ellipse(this.x, this.y, this.diameter, this.diameter);

    if(this.diameter + this.inc >= MAX_DOT && this.inc > 0 || this.diameter <= 0 && this.inc < 0){
      this.inc = -1 * this.inc;
    }else{
      this.diameter += this.inc;
    }
  }
}

