function draw() {
  background("#600");
  noSmooth();

  if (frameCount % params.spawnDelay === 0) spawnHeart();

  hearts.forEach(stepHeart);
  hearts.forEach((heart) => {
    if (heart.y < -10) removeArrayItem(hearts, heart);
  });

  hearts.forEach(drawHeart);
}

function stepHeart(heart) {
  // float
  heart.y += params.floatForce;
  const t = frameCount * params.flowMutate;
  function n(x, y, z) {
    return (
      map(
        noise(x * params.flowScale, y * params.flowScale, z + t),
        0,
        0.75,
        -1,
        1
      ) * params.flowForce
    );
  }

  // flow
  heart.x += n(heart.x, heart.y, 10);
  heart.y += n(heart.x, heart.y, 20);
}

function spawnHeart() {
  hearts.push({
    x: random(width),
    y: height + 10,
  });
}

function drawHeart(heart) {
  push();
  translate(heart.x, heart.y);
  //   rotate(heart.r);
  image(heartImage, -10, -10, 21, 21);
  pop();
}

function removeArrayItem(a, item) {
  const index = a.indexOf(item);
  a.splice(index, 1);
}

// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js
//[Flow Field] (https://www.youtube.com/watch?v=BjoM9oKOAKY&list=PLRqwX-V7Uu6ZiZxtDDRCi6uhfTH4FilpH&index=28)
//[Nature of Code] (https://www.youtube.com/playlist?list=PLRqwX-V7Uu6ZV4yEcW3uDwOgGXKUUsPOM)
//1-8: Perlin noise 2D, 3D, simplex noise
//31-36: Particle systems

const inc = 0.05;
const scale = 10;
let cols, rows;
let zoff = 0;
let particles = [];
let flowField;

class Particle {
    constructor() {
        this.pos = createVector(random(width), random(height));
        this.vel = p5.Vector.random2D();
        this.acc = createVector(0, 0);
        this.maxspeed = 4;
        this.prevPos = this.pos.copy();

        this.update = function () {
            this.vel.add(this.acc);
            this.vel.limit(this.maxspeed);
            this.pos.add(this.vel);
            this.acc.mult(0);
        }
        this.applyForce = function (force) {
            this.acc.add(force)
        }

        this.show = function () {
            stroke(0, 5);
            strokeWeight(1);
            // stroke(random(15), random(255), random(105), 5);
            // point(this.pos.x, this.pos.y);
            line(this.pos.x, this.pos.y, this.prevPos.x, this.prevPos.y)
            this.updatePrev();
        }

        this.updatePrev = function () {
            this.prevPos.x = this.pos.x;
            this.prevPos.y = this.pos.y;
        }
        this.edges = function () {
            if (this.pos.x > width) {
                this.pos.x = 0;
                this.updatePrev();
            }
            if (this.pos.x < 0) {
                this.pos.x = width;
                this.updatePrev();
            }
            if (this.pos.y > height) {
                this.pos.y = 0;
                this.updatePrev();
            }
            if (this.pos.y < 0) {
                this.pos.y = height;
                this.updatePrev();
            }
        }

        this.follow = function (vectors) {
            let x = floor(this.pos.x / scale);
            let y = floor(this.pos.y / scale);
            let index = x + y * cols;
            let force = vectors[index];
            this.applyForce(force);
        }


    }


}

function setup() {
    createCanvas(500, 500);
    background(255);

    cols = floor(width / scale);
    rows = floor(height / scale);

    flowField = new Array(cols * rows);

    for (let i = 0; i < 10000; i++) {
        particles[i] = new Particle();
    }


}

function draw() {


    let yoff = 0;

    for (let y = 0; y < rows; y++) {
        let xoff = 0;

        for (let x = 0; x < cols; x++) {
            let r = noise(xoff, yoff) * 255;

            let index = (x + y * cols);
            let angle = noise(xoff, yoff, zoff) * TWO_PI * 2;

            //straight right pointing lines
            // let v = p5.Vector.fromAngle(0);

            //straight downward pointing lines
            // let v = p5.Vector.fromAngle(PI / 2);

            //straight 60 deg pointing lines
            // let v = p5.Vector.fromAngle(PI / 6);

            //random TWO PI angle
            // let v = p5.Vector.fromAngle(random(TWO_PI));

            let v = p5.Vector.fromAngle(angle);
            v.setMag(1);
            flowField[index] = v;
            xoff += inc;
            stroke(0, 50);

            //hide the vectors to speed up the particle rendering
            // push();
            // translate(x * scale, y * scale);
            // rotate(v.heading());
            // line(0, 0, scale, 0);
            // pop();

            // rect(x * scale, y * scale, scale, scale)
        }
        yoff += inc;
        zoff += 0.0001;
    }

    for (let i = 0; i < particles.length; i++) {
        particles[i].edges();
        particles[i].update();
        particles[i].show();
        particles[i].follow(flowField);
    }

    // noLoop();
}

// require https://cdnjs.cloudflare.com/ajax/libs/paper.js/0.12.15/paper-full.min.js
// require https://cdn.jsdelivr.net/npm/tweakpane@3.0.7/dist/tweakpane.min.js
// require https://cdnjs.cloudflare.com/ajax/libs/chroma-js/2.4.2/chroma.min.js

// parameteres
const PARAMS = {};

const allLines = [];

let bounds = null;
let rectangle = null;

let circle1 = null;
let deltaPosition = null;

const VIEW_HEIGHT = 500;

const LINE_GAP = 10;
const WAVE_WIDTH = 20;
const SINE_PHASE = VIEW_HEIGHT / 22;

let pane = null;
let patternNumber = 0;

const setupTweakPane = () => {
  const pane = new Tweakpane.Pane();

  for (p in PARAMS) {
    pane.addInput(PARAMS, p);
  }

  pane.on("change", () => {
    allLines.forEach((pattern) => {
      pattern.bg.fillColor = PARAMS[pattern.colorKey];
      const c2 = chroma(PARAMS[pattern.colorKey]).darken().hex();
      pattern.lines.forEach((l) => {
        l.strokeColor = c2;
      });
    });
  });
};

const createPattern = (posX, w, c1 = "#E29393", sY = 1.5) => {
  let bounds = null;
  bounds = new paper.Rectangle(
    new paper.Point(posX, 0),
    new paper.Point(posX + w, VIEW_HEIGHT)
  );

  PARAMS[`color_${patternNumber}`] = c1;
  const c2 = chroma(c1).darken().hex();

  const bg = new paper.Path.Rectangle(bounds);
  bg.fillColor = c1;

  const lines = [];

  for (let x = 0; x < bounds.width; x += LINE_GAP) {
    const wiggleLine = new paper.Path();
    wiggleLine.strokeColor = c2;

    let shiftY = 0;
    if (x % (LINE_GAP * 2)) {
      shiftY = sY;
    }

    for (let y = 0; y <= SINE_PHASE; y++) {
      const thisX =
        posX +
        x +
        Math.sin(Math.PI * ((y + shiftY) / 3)) * (WAVE_WIDTH / 2) +
        WAVE_WIDTH / 2;
      wiggleLine.add(new paper.Point(thisX, y * SINE_PHASE));
    }

    wiggleLine.smooth();
    lines.push(wiggleLine);
  }

  allLines.push({
    colorKey: `color_${patternNumber}`,
    number: patternNumber,
    bg: bg,
    lines: lines,
  });

  patternNumber++;
};

window.onload = () => {
  // Get a reference to the canvas object
