function draw() {

  let noiseFrequency = 3;

  for (let i = 0; i < 100; i++) {

    placePoint(i, width, height, noiseFrequency);

    updatePt(points[i]);
    showPt(points[i]);
  }
}

function placePoint(i, w, h, freq) {
  let x = (noise(i * freq, 0) - 0.5) * w;
  let y = (noise(i * freq, 1000) - 0.5) * h;

  let param = (random() + random()) / 2;
  let diameter = map(param, 0, 1, 8, 16);

  points.push({
    x: x,
    y: y,
    d: diameter,
    angle: 0,
    maxAngle: random(45, 315),
    rate: random(0.4, 0.6),
    hue: (param - 0.15) % 1
  })
}

function updatePt(pt) {
  if(pt.angle <= pt.maxAngle) {
    pt.angle += pt.rate;
    pt.d -= 0.05;
  }
}

function showPt(pt) {
  push();
  translate(width/2, height/2);
  rotate(pt.angle);
  fill(pt.hue, 1, 1);
  ellipse(pt.x, pt.y, pt.d);
