//require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js
//incorporated processes from this Processing video tutorial
//[Starfield](https://www.youtube.com/watch?v=17WoOqgXsRM&list=PLRqwX-V7Uu6ZiZxtDDRCi6uhfTH4FilpH)

let elements = [];
let speed;
let count = 500;
let rectInc = 50;
let weight = 4;
let posColor = 0;
let colors = ["#ffc300", "#ba181b", "#03045e", "#ffffff"]
let lineX = 30;


function setup() {
  createCanvas(800, 800);
  colorMode(HSB);
  noLoop();

  function Element() {
    this.x = random(-width, width);
    this.y = random(-height, height);
    this.z = random(width);
    this.pz = this.z;

    this.show = function () {
      noStroke();
      fill('#8d99ae');
      let sx = map(this.x / this.z, 0, 1, 0, width);
      let sy = map(this.y / this.z, 0, 1, 0, height);
      let r = map(this.z, 0, width, 150, 5);
      let px = map(this.x / this.pz, 0, 1, 0, width);
      let py = map(this.y / this.pz, 0, 1, 0, height);
      this.pz = this.z;
      rectMode(CENTER);
      stroke(0);
      strokeWeight(weight);
      fill(colorShuffle());
      rect(sx - rectInc, sy - rectInc, r, r);
      noStroke;
      strokeWeight(weight);
      rect(sx - rectInc * 2, sy - rectInc * 3, 1, random(0, height / 2));
      rect(sx - rectInc * 2, sy - rectInc * 3, random(0, height / 2), 1);
    }

    this.update = function () {
      this.z -= speed;

      if (this.z < 1) {
        this.z = width;
        this.x = random(-width, width);
        this.y = random(-height, height);
        this.pz = this.z;
      }
    }

  }

  for (let i = 0; i < count; i++) {

    elements[i] = new Element();
    lineX++;
  }
}

function draw() {
  background(255);
  translate(width / 2, height / 2);
  speed = map(mouseX, 0, width, 2, 5);
  for (element of elements) {
    element.show();
    element.update();
  }
}


function colorShuffle() {
  let v = colors[posColor];
  posColor++;
  if (posColor == colors.length) {
    colors = shuffle(colors);
    posColor = 0;
  }
  return v;
}

// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js

const TEXTSIZE = 10;

function setup() {
  createCanvas(windowWidth, windowHeight);
  textSize(TEXTSIZE);

  frameRate(10);
  // noLoop();
}


function draw() {
  background(255);

  // wigglyStripes("memory", "thicket", 2, 1);
  // wigglyTriangles("memory", "thicket");
  wigglyCircle("and", "tit", 30);

}


function wigglyCircle(w1, w2, r){
  push();
  translate(width / 2, height / 2);

  let width1 = textWidth(w1);
  let width2 = textWidth(w2);
  let avgwidth = (width1 + width2) / 2;
  let radius = r * width2;

  for(let k = -height / 2; k < height / 2; k += TEXTSIZE){
    let s = "";
    let increment = width1;
    let wiggle = sin(frameCount + k);
    for(let i = -width / 2; i < width / 2; i += increment){
      if(dist(i, k, 0, 0) < radius){
        s += w2;
        increment = width2;
      }else{
        s += w1;
        increment = width1;
      }
    }
    text(s, -width / 2 + wiggle, k);
  }

  pop();
}

function wigglyTriangles(w1, w2){
  push();
  translate(width / 2, height / 2);

  let width1 = textWidth(w1);
  let width2 = textWidth(w2);

  for(let k = -height / 2; k < height / 2; k += TEXTSIZE){
    let offset = abs(k / TEXTSIZE * width2);
    let wiggle = sin(frameCount + offset);
    let s = "";

    for(let i = 0; i < width / 2 - offset / 2; i += width1){
      s += w1;
    }
    for(let i = width / 2 - offset / 2; i < width / 2 + offset / 2; i += width2){
      s += w2;
    }
    for(let i = width / 2 + offset / 2; i < width; i += width1){
      s += w1;
    }
    text(s, -width / 2 + wiggle, k);
  }

  pop();
}

function wigglyStripes(w1, w2, l1, l2){
  let s = createString(w1, w2, l1, l2);
  let w = textWidth(s);
  for(let i = 0; i < height; i += TEXTSIZE){
    let wiggle = sin(frameCount + i / TEXTSIZE);
    for(let k = 0; k < width; k += w){
      text(s, k + wiggle, i);
    }
  }
}

function createString(w1, w2, l1, l2){
  let s = "";
  for(let i = 0; i < l1; i++){
    s = s + w1;
  }
  for(let i = 0; i < l2; i++){
    s = s + w2;
  }
  for(let i = 0; i < l1; i++){
    s = s + w1;
  }

  return s;
}

