// require https://cdn.jsdelivr.net/npm/p5@latest/lib/p5.min.js
// require https://cdn.jsdelivr.net/npm/tweakpane@3.0.7/dist/tweakpane.min.js

const HEIGHT = 800;
const WIDTH = 800;

let hairImgs = [];
let eyeImgs = []; 
let browImgs = []; 
let mouthImgs = [];
let noseImgs = [];

const pane = new Tweakpane.Pane();

let hair, eyes, mouth, brows, nose;

function preload(){
  // eyes
  for(let i = 0; i < 3; i++){
    eyeImgs.push(loadImage("images/eye" + (i + 1) + ".png"));
  }
  // eyebrows
  for(let i = 0; i < 2; i++){
    browImgs.push(loadImage("images/eyebrow" + (i + 1) + ".png"));
  }
  // hairs
  for(let i = 0; i < 4; i++){
    hairImgs.push(loadImage("images/hair" + (i + 1) + ".png"));
  }
  // mouths
  for(let i = 0; i < 5; i++){
    mouthImgs.push(loadImage("images/mouth" + (i + 1) + ".png"));
  }
  // noses
  for(let i = 0; i < 3; i++){
    noseImgs.push(loadImage("images/nose" + (i + 1) + ".png"));
  }
}

function setup() {
  angleMode(DEGREES);
  imageMode(CENTER);
  createCanvas(WIDTH, HEIGHT);

  hair = new Feature('hair', hairImgs, {long:1, short:0, reallyshort:3, spiky:2}, WIDTH / 2, HEIGHT / 2, 1, 0);
  eyes = new Feature('eyes', eyeImgs, {a:0, b:1, c:2}, WIDTH / 2 - hairImgs[0].width / 4, hair.params.y + 2 * eyeImgs[0].height, 1, 0, hairImgs[0].width / 2);
  brows = new Feature('brows', browImgs, {a:0, b:1}, eyes.params.x, eyes.params.y - eyeImgs[0].height, 1, 0, hairImgs[0].width / 2);
  mouth = new Feature('mouth', mouthImgs, {a:0, b:1, c:2, d:3, e:4}, WIDTH / 2, eyes.params.y + 3 * eyeImgs[0].height, 1, 0);
  nose = new Feature('nose', noseImgs, {a:0, b:1, c:2}, WIDTH / 2, eyes.params.y + eyeImgs[0].height, 1, 0);
};

function draw() {
  background(255);

  hair.draw();
  eyes.draw();
  brows.draw();
  mouth.draw();
  nose.draw();
}


Feature.prototype.draw = function(){
  push();
  translate(this.params.x, this.params.y);
  push();
  rotate(this.params.angle);
  image(this.imgs[this.params.type], 0, 0);
  pop();
  // draw a second copy for eyes and brows 
  if(this.params.dist != undefined){
    push();
    translate(this.params.dist, 0);
    rotate(-this.params.angle);
    image(this.imgs[this.params.type], 0, 0);
    pop(); 
  }
  pop();
}

function Feature(name, imgs, types, x, y, size, angle, dist){
  // hold images / name
  this.imgs = imgs;
  this.name = name;

  // add parameters
  this.params = {
    x: x,
    y: y,
    size: size,
    angle: angle,
    type: 0
  }

  // add the distance parameter for some features
  if(dist != undefined){
    this.params.dist = dist;
  }

  // array for key / value pairs
  let keyNames = Object.keys(this.params);
  let keyValues = Object.values(this.params);

  // add a pane folder
  this.paneFolder = pane.addFolder({title: this.name, expanded: true}); 

  // add parameters to the pane folder for each feature
  for(let i = 0; i < Object.keys(this.params).length; i++){
    console.log(keyNames[i]);
    // handle the list options
    if(keyNames[i] == 'angle'){
      this.paneFolder.addInput(this.params, 'angle', {min: 0, max: 306});
    }else if(keyNames[i] == 'type'){
      this.paneFolder.addInput(this.params, 'type', {options: types});
    }else{
      // make ranged inputs equal to 0 2x the input (this could be mapped instead)
      this.paneFolder.addInput(this.params, keyNames[i], { min: 0, max: keyValues[i] * 2});
    }
  }
}

