class customLine {
  constructor(x, y, w, h) {
    this.xStart = x;
    this.yStart = y;
    this.w = w;
    this.h = h;
    this.startAngle = random([0, 90, 180, 270]);
    this.shapeIndex = random([1,2,3,4,5,6,7]);
    this.shapeDecreaseIndex = random(0.5, 0.8);
    this.fillState = random([0,1]);
    this.strokeVariation = random(1, 3);
    this.distanceVector = floor(map(dist(x, y, width/2, y), 0, width/2, 255, 120));
    this.startOp = 0;
  }

  createLine() {
    push();
      translate(this.xStart, this.yStart);
      beginShape();
        if (this.shapeIndex === 1 || this.shapeIndex === 4) {
          noFill();
          stroke(233, 30, 99, this.startOp);
          strokeWeight(this.strokeVariation);
          arc(0, 0, this.w, this.h, this.startAngle, this.startAngle + 90);
        }
        else if (this.shapeIndex === 2) {
          if (this.fillState === 1) {
            noStroke();
            fill(239, 83, 80, this.startOp);
          }
          else {
            noFill();
            stroke(240, 98, 146, this.startOp);
          }
          circle(0,0,this.w * this.shapeDecreaseIndex);
        }
        else if (this.shapeIndex === 3) {
          if (this.fillState === 1) {
            noStroke();
            fill(233, 30, 99, this.startOp);
          }
          else {
            noFill();
            stroke(233, 30, 99, this.startOp);
          }
          rect(0,0, this.w* this.shapeDecreaseIndex, this.h * this.shapeDecreaseIndex);
        }
        else if (this.shapeIndex === 5) {
          if (this.fillState === 1) {
            fill(233, 30, 99, this.startOp);
            noStroke();
          }
          else {
            noFill();
            stroke(233, 30, 99, this.startOp);
          }
          triangle((this.w * this.shapeDecreaseIndex)/2, this.h * ((1- this.shapeDecreaseIndex)/2), this.w * ((1 - this.shapeDecreaseIndex)/2) , this.h * this.shapeDecreaseIndex + (this.h * ((1 - this.shapeDecreaseIndex)/2)),this.w * this.shapeDecreaseIndex , this.h * this.shapeDecreaseIndex + (this.h * ((1 - this.shapeDecreaseIndex)/2)));
        } 
      endShape();
    pop();
  }

  display() {
    this.startOp = lerp(this.startOp, this.distanceVector, 0.5);
  }
}

