function createFlower (center, size, fill1, fill2) {
    for (var i = 1; i < 6; i++) {
        var topLeft = center - new Point(size,size);
        var bottomRight = center + new Point(size,size);

        var rect = new Rectangle(
            topLeft, 
            bottomRight
        );
        var path = new Path.Rectangle(rect);
        path.rotate(i * 360/5, center);
        path.fillColor = {
            gradient: {
                stops: [[fill1, 0.2], [fill2, 1]],
                radial: true
            },
            origin: path.position,
            destination: path.bounds.rightCenter
        };
        path.blendMode = 'multiply';
    } 
}

function createPoligonFlower(center, fillArr) {
    var fill = fillArr[Math.floor(Math.random() * fillArr.length)];
    for (var i = 1; i < 6; i++) {
        var poly = new Path.RegularPolygon({
            center: center,
            sides: Math.floor(Math.random() * 8),
            radius: 40,
            fillColor: fill
        });
        poly.rotate(i * 360/5, center);
        poly.blendMode = 'multiply';
    }
}

