// create a randomly rotated fragment
module frag(size){
    length = size+rands(-size/4,size/4,1)[0];
    width = size+rands(-size/4,size/4,1)[0];
    height = size/2+rands(-size/4,size/4,1)[0];

    // lift the box onto the ground
    translate([0,0,height/2])
    rand_box(length, width, height);
}

// create a random box
module rand_box(l, w, h){
    dl = l/8;
    dw = w/8;
    dh = h/8;

    CubePoints = [
    [-l/2+rands(-dl, dl, 1)[0], -w/2+rands(-dw, dw, 1)[0], -h/2+rands(-dh, dh, 1)[0]], //0
    [l/2+rands(-dl, dl, 1)[0], -w/2+rands(-dw, dw, 1)[0], -h/2+rands(-dh, dh, 1)[0]], //1
    [l/2+rands(-dl, dl, 1)[0], w/2+rands(-dw, dw, 1)[0], -h/2+rands(-dh, dh, 1)[0]], //2
    [-l/2+rands(-dl, dl, 1)[0], w/2+rands(-dw, dw, 1)[0], -h/2+rands(-dh, dh, 1)[0]], //3
    [-l/2+rands(-dl, dl, 1)[0], -w/2+rands(-dw, dw, 1)[0], h/2+rands(-dh, dh, 1)[0]], //4
    [l/2+rands(-dl, dl, 1)[0], -w/2+rands(-dw, dw, 1)[0], h/2+rands(-dh, dh, 1)[0]], //5
    [l/2+rands(-dl, dl, 1)[0], w/2+rands(-dw, dw, 1)[0], h/2+rands(-dh, dh, 1)[0]], //6
    [-l/2+rands(-dl, dl, 1)[0], w/2+rands(-dw, dw, 1)[0], h/2+rands(-dh, dh, 1)[0]] //7
    ];

    CubeFaces = [
    [0,1,2,3],  // bottom
    [4,5,1,0],  // front
    [7,6,5,4],  // top
    [5,6,2,1],  // right
    [6,7,3,2],  // back
    [7,4,0,3]]; // left

    polyhedron( CubePoints, CubeFaces );
}

// create tile pile
pile_scope = 1200;
pile_height = 900;

for(i=[0:600]){
    divider = sqrt((pile_scope/2)^2*2);

    dist = rands(0, pile_scope, 1)[0];
    deg = rands(0,360,1)[0];

    pos = [dist*cos(deg), dist*sin(deg)];
    height = max(pile_height-pile_height*sqrt(sqrt(pos[0]^2+pos[1]^2))/sqrt(divider),0);

    deg_x = pos[1]>0 ? (pile_scope/2 + pos[1])*(90/(pile_scope/2)) : -(pile_scope/2 - pos[1])*(90/150) ;
    deg_y = pos[0]>0 ? -(pile_scope/2 + pos[0])*(90/(pile_scope/2)) : (pile_scope/2 - pos[0])*(90/150);

    translate([pos[0], pos[1], height])
    rotate([deg_x, deg_y, rands(0,180,1)[0]])
    frag(rands(20,20+120*sqrt(pos[0]^2+pos[1]^2)/divider,1)[0]);
}

