// This code started from Justin Bakse's second take-home challenge on https://compform.net/strategy/

let frequency_slider, amplitude_slider, speed_slider;

// position of furthest left ellipse
let startX = -10;
let startY = 300;

// position of furthest right ellipse
let endX = 500;
let endY = -10;

// hue 
let h = 300;
let hueDirect = true;

function setup() {
  createCanvas(500, 300);
  colorMode(HSB);

  createP("Frequency");
  frequency_slider = createSlider(0, 100, 15);

  createP("Amplitude");
  amplitude_slider = createSlider(0, 200, 50);

  createP("Time Speed");
  speed_slider = createSlider(0, 100, 50);

  createP("Density");
  density_slider = createSlider(1, 50, 15);
}

function draw() {
  background(50, 0, 0, 0.02);
  fill(150, 80, 100);
  noStroke();

  let density = 51 - density_slider.value();

  for(let i = -250; i < 200; i+=density){
    makeSquares(i, i);
  }

}

function makeSquares(x, y){
  push();
  translate(x, y);
  let frequency = frequency_slider.value() / 1000;
  let amplitude = amplitude_slider.value();
  let speed = speed_slider.value() / 1000;

  noiseDetail(1, 0.1);

  for (i = 0.02; i < 1; i += 0.04) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    let offsetX = (noise(x * frequency + frameCount * speed)) * amplitude;
    let offsetY = (noise(y * frequency + frameCount * speed)) * amplitude;

    // I wanted to limit my hue from 150Âº to 330Âº
    changeHue();
    if(hueDirect){
      h += 0.005;
    } else if(!hueDirect){
      h -= 0.005;
    }

    fill(h, 70, 80, 0.8);
    rect(x + offsetX, y + offsetY, 10, 10);
  }
  pop();
}

function changeHue(){
  if(h>330){
    hueDirect = false;
  } else if (h<150){
    hueDirect = true;
  }
}

