let mousePoint2;

window.onload = function () {
  // Setup directly from canvas id:
  paper.setup("myCanvas");
  mousePoint2 = view.center;
  const randOne = Math.random() * 10;
  const randTwo = Math.random() * 3;
  const randThree = Math.random() * 5;
  // console.log(randOne, randTwo, randThree);

  for (var i = 0; i < amount; i++) {
    var rect = new Rectangle([0, 0], [15, amount - i / randOne]);

    rect.center = mousePoint2;
    var path = new Path.Rectangle(rect, amount - i * randTwo);
    var scale = (1 - (i * randThree) / amount) * 15;
