    moveBall(ball);
  }
}

function moveBall(ball){
  ball.x = ball.x + ball.deltaX;
  ball.y=ball.y+ ball.deltaY;

  if (ball.x>500){
    ball.deltaX = -abs(ball.deltaX);
  }

  if (ball.y>500){
    ball.deltaY = -abs(ball.deltaY);
  }

  if (ball.x<0){
    ball.deltaX=abs(ball.deltaX);
  }

  if (ball.y<0){
    ball.deltaY=abs(ball.deltaY);
  }
}

