function pickTerrain(x, z){
  let h = noise((x)*noiseScale, (z)*noiseScale, frameCount*0.01);

  if (h < 0.2){  c = c1; } 
  else if (h < 0.3) { c = c2; } 
  else if (h < 0.4) { c = c3; }
  else if (h < 0.58) { c = c4; }
  else if (h < 0.7) { c = c5; }
  else { c = c6; }

  return color(c);

}

