class Circle {
	constructor(color, radius) {
		this.x = random(width)
		this.y = random(height)
		this.newX = random(width)
		this.newY = random(height)
		this.color = color
		this.radius = radius
		this.angle = 0
	}

	draw() {
		let scale = 50
		for (let i = 0; i < scale; i++) {
			let colorNow = color(this.color)
			colorNow.setAlpha(i)
			fill(colorNow)
			noStroke()
			ellipse(this.x, this.y, this.radius * (scale - i) / scale)
		}
	}

	resetNew() {
		this.newX = random(width)
		this.newY = random(height)
	}

	move() {
		this.x = lerp(this.x, this.newX, 0.1)
		this.y = lerp(this.y, this.newY, 0.1)
	}

	resetAngle(i) {
		this.angle = i * 120
	}

	rotateNew(i) {
		this.newX = width / 2 + 150 * cos(this.angle)
		this.newY = height / 2 + 150 * sin(this.angle)
		this.angle += 5
	}
}

